
public class Unit3_Disscusion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int cooking = 0;
	while (cooking <= 2) {
		System.out.printf("Mommy, we are hungry!\n");
		cooking++;

		if (cooking == 1)
		{
			System.out.printf("Breakfast is ready!! %d\n", cooking);
			
		}
		if (cooking == 2) {
		System.out.printf("Dinner is ready!! %d\n", cooking);
		break;
		}
		
	}
}
}
